const sel = (q, root = document) => root.querySelector(q);
const selAll = (q, root = document) => [...root.querySelectorAll(q)];

function announce(msg) {
  const live = sel('#live-regiao');
  if (!live) return;
  live.textContent = '';
  setTimeout(() => { live.textContent = msg; }, 50);
}

function toggleContraste(){
  document.body.classList.toggle('alto-contraste');
  announce('Modo de alto contraste alternado.');
}

function ajustarFonte(delta){
  const atual = parseFloat(getComputedStyle(document.body).fontSize);
  const novo = Math.max(14, Math.min(24, atual + delta));
  document.body.style.fontSize = novo + 'px';
  announce('Tamanho da fonte ajustado para ' + Math.round(novo) + ' pixels.');
}

function toggleSimplificado(){
  document.body.classList.toggle('simplificado');
  announce('Modo simplificado alternado.');
}

function lerPagina(){
  const main = sel('#conteudo-principal');
  if (!main) return;
  const texto = main.innerText.trim();
  if ('speechSynthesis' in window){
    window.speechSynthesis.cancel();
    const u = new SpeechSynthesisUtterance(texto);
    u.lang = 'pt-BR';
    u.rate = 0.95;
    window.speechSynthesis.speak(u);
  } else {
    alert('Leitura de voz não suportada neste navegador.');
  }
}



const STORAGE_KEY = 'pratafarma:carrinho';

function getCarrinho(){
  try { return JSON.parse(localStorage.getItem(STORAGE_KEY)) || []; }
  catch { return []; }
}
function setCarrinho(itens){
  localStorage.setItem(STORAGE_KEY, JSON.stringify(itens));
}

function addAoCarrinho(id, qtd){
  const carrinho = getCarrinho();
  const idx = carrinho.findIndex(i => i.id === id);
  if (idx >= 0) { carrinho[idx].qtd += qtd; }
  else { carrinho.push({ id, qtd }); }
  setCarrinho(carrinho);
  const prod = CATALOGO.find(p => p.id === id);
  announce(`${prod?.nome || 'Produto'} adicionado ao carrinho.`);
}

function removerDoCarrinho(id){
  const carrinho = getCarrinho().filter(i => i.id !== id);
  setCarrinho(carrinho);
  announce('Item removido do carrinho.');
}

function atualizarQtd(id, qtd){
  const carrinho = getCarrinho();
  const item = carrinho.find(i => i.id === id);
  if (item){
    item.qtd = Math.max(1, qtd|0);
    setCarrinho(carrinho);
  }
}



function renderHome(){
  const lista = sel('#lista-produtos');
  if (!lista) return;

  const campoBusca = sel('#campo-busca');
  const btnBuscar = sel('#btn-buscar');

  function desenhar(produtos){
    lista.innerHTML = '';
    produtos.forEach(p => {
      const card = document.createElement('article');
      card.className = 'card';
      card.tabIndex = 0;
      card.setAttribute('aria-labelledby', `ttl-${p.id}`);

      const preco = p.prescricao ? '—' : `R$ ${p.preco.toFixed(2)}`;
      const disabled = p.prescricao ? 'disabled' : '';

      card.innerHTML = `
        <img src="${p.imagem}" alt="Imagem de ${p.nome}">
        <h3 id="ttl-${p.id}">
          <svg class="icon" aria-hidden="true" focusable="false"><use href="icons.svg#i-bottle"></use></svg>
          ${p.nome}
        </h3>
        <p class="preco">${preco}</p>
        <p><span class="badge">${p.categoria}</span> <span class="badge">${p.indicacao}</span></p>
        <div class="acoes">
          <a href="produto.html?id=${encodeURIComponent(p.id)}">
            <svg class="icon" aria-hidden="true" focusable="false"><use href="icons.svg#i-bottle"></use></svg>
            Ver detalhes
          </a>
          <button ${disabled} data-id="${p.id}" aria-disabled="${p.prescricao?'true':'false'}">
            ${p.prescricao
              ? '<svg class="icon" aria-hidden="true" focusable="false"><use href="icons.svg#i-bottle"></use></svg> Requer receita'
              : '<svg class="icon" aria-hidden="true" focusable="false"><use href="icons.svg#i-cart"></use></svg> Adicionar'}
          </button>
        </div>
      `;
      lista.appendChild(card);
    });

    selAll('.acoes button', lista).forEach(btn => {
      btn.addEventListener('click', () => {
        const id = btn.getAttribute('data-id');
        const prod = CATALOGO.find(p => p.id === id);
        if (!prod || prod.prescricao) return;
        addAoCarrinho(id, 1);
      });
    });
  }

  function filtrar(){
    const q = (campoBusca.value || '').toLowerCase().trim();
    const res = CATALOGO.filter(p =>
      [p.nome, p.categoria, p.indicacao].join(' ').toLowerCase().includes(q)
    );
    desenhar(res);
  }

  btnBuscar?.addEventListener('click', filtrar);
  campoBusca?.addEventListener('keydown', e => { if (e.key === 'Enter') filtrar(); });

  desenhar(CATALOGO);
}

function renderProduto(){
  const el = sel('#produto');
  if (!el) return;

  const params = new URLSearchParams(location.search);
  const id = params.get('id');
  const p = CATALOGO.find(x => x.id === id) || CATALOGO[0];

  sel('#produto-nome').textContent = p.nome;
  const img = sel('#produto-imagem');
  img.src = p.imagem;
  img.alt = `Imagem ilustrativa de ${p.nome}`;
  sel('#produto-imagem-legenda').textContent = p.nome;

  sel('#produto-descricao').textContent = p.descricao;
  sel('#produto-categoria').textContent = p.categoria;
  sel('#produto-indicacao').textContent = p.indicacao;
  sel('#produto-restricao').textContent = p.restricao;

  const precoEl = sel('#produto-preco [itemprop="price"]');
  if (p.prescricao){
    precoEl.textContent = '—';
    sel('#form-comprar').classList.add('oculto');
    sel('#alerta-receita').classList.remove('oculto');
  } else {
    precoEl.textContent = p.preco.toFixed(2);
  }

  sel('#form-comprar')?.addEventListener('submit', (e) => {
    e.preventDefault();
    if (p.prescricao) return;
    const qtd = Math.max(1, parseInt(sel('#qtd').value, 10) || 1);
    addAoCarrinho(p.id, qtd);
  });

  // relacionados (mesma categoria)
  const rel = CATALOGO.filter(x => x.id !== p.id && x.categoria === p.categoria).slice(0, 4);
  const box = sel('#lista-relacionados');
  box.innerHTML = '';
  rel.forEach(r => {
    const a = document.createElement('a');
    a.className = 'card';
    a.href = `produto.html?id=${encodeURIComponent(r.id)}`;
    a.innerHTML = `
      <img src="${r.imagem}" alt="Imagem de ${r.nome}">
      <h3>${r.nome}</h3>
      <p class="preco">${r.prescricao ? '—' : 'R$ ' + r.preco.toFixed(2)}</p>
    `;
    box.appendChild(a);
  });
}

function renderCarrinho(){
  const lista = sel('#itens-carrinho');
  if (!lista) return;

  function formatBR(v){ return v.toLocaleString('pt-BR', {minimumFractionDigits:2}); }

  function desenhar(){
    const carrinho = getCarrinho();
    lista.innerHTML = '';
    let subtotal = 0;

    if (carrinho.length === 0){
      const vazio = document.createElement('p');
      vazio.textContent = 'Seu carrinho está vazio.';
      lista.appendChild(vazio);
    } else {
      carrinho.forEach(item => {
        const p = CATALOGO.find(x => x.id === item.id);
        if (!p) return;
        const linha = document.createElement('div');
        linha.className = 'item';
        const totalItem = p.preco * item.qtd;
        subtotal += totalItem;

        linha.innerHTML = `
          <img src="${p.imagem}" alt="${p.nome}">
          <div>
            <strong>
              <svg class="icon" aria-hidden="true" focusable="false"><use href="icons.svg#i-bottle"></use></svg>
              ${p.nome}
            </strong>
            <div class="muted">${p.categoria} · ${p.indicacao}</div>
            <div class="muted">${p.prescricao ? 'Requer receita' : 'Sem prescrição'}</div>
          </div>
          <div class="qtd">
            <label class="sr-only" for="qtd-${p.id}">Quantidade</label>
            <input id="qtd-${p.id}" type="number" min="1" value="${item.qtd}">
          </div>
          <div>
            <div>R$ ${formatBR(totalItem)}</div>
            <button class="remover" data-id="${p.id}" aria-label="Remover ${p.nome} do carrinho">
              <svg class="icon" aria-hidden="true" focusable="false"><use href="icons.svg#i-trash"></use></svg>
              <span class="sr-only">Remover</span>
            </button>
          </div>
        `;
        lista.appendChild(linha);

        linha.querySelector('input').addEventListener('change', (e) => {
          const nova = Math.max(1, parseInt(e.target.value, 10) || 1);
          atualizarQtd(p.id, nova);
          desenhar();
        });
        linha.querySelector('.remover').addEventListener('click', () => {
          removerDoCarrinho(p.id);
          desenhar();
        });
      });
    }

    document.querySelector('#total-produtos').textContent = 'R$ ' + formatBR(subtotal);
    const frete = subtotal > 0 ? 14.90 : 0;
    document.querySelector('#frete').textContent = 'R$ ' + formatBR(frete);
    document.querySelector('#total-geral').textContent = 'R$ ' + formatBR(subtotal + frete);
  }

  desenhar();

  document.querySelector('#btn-calcular-frete')?.addEventListener('click', () => {
    const cep = (document.querySelector('#cep').value || '').trim();
    if (!/^\d{5}-?\d{3}$/.test(cep)){
      announce('CEP inválido.');
      return;
    }
    announce('Frete estimado para o CEP informado.');
  });

  document.querySelector('#btn-finalizar')?.addEventListener('click', () => {
    announce('Fluxo de checkout guiado não implementado neste protótipo.');
    alert('Demonstração: aqui entraria o checkout guiado (RF10), validações e métodos de pagamento.');
  });
}


function bindAcessibilidade(){
  document.querySelector('#btn-contraste')?.addEventListener('click', toggleContraste);
  document.querySelector('#btn-fonte-mais')?.addEventListener('click', () => ajustarFonte(2));
  document.querySelector('#btn-fonte-menos')?.addEventListener('click', () => ajustarFonte(-2));
  document.querySelector('#btn-simplificado')?.addEventListener('click', toggleSimplificado);
  document.querySelector('#btn-ler')?.addEventListener('click', lerPagina);
}

document.addEventListener('DOMContentLoaded', () => {
  bindAcessibilidade();
  renderHome();
  renderProduto();
  renderCarrinho();
});
